$user_id = 1;

//   
if (filter_has_var(INPUT_GET, 'act') && filter_input(INPUT_GET, 'act', FILTER_UNSAFE_RAW) === 'post')
{
  $type = filter_input(INPUT_POST, 'type', FILTER_UNSAFE_RAW);
  
  if ($type === 'post_review')
  {
    $review = filter_input_array(INPUT_POST, array(
        'speed' => FILTER_VALIDATE_INT,
        'responsibility' => FILTER_VALIDATE_INT,
        'quality' => FILTER_VALIDATE_INT,
        'summary' => FILTER_VALIDATE_INT,
        'parent' => FILTER_VALIDATE_INT,
        'language' => array(
                        'filter' => FILTER_VALIDATE_INT,
                        'flags'  => FILTER_REQUIRE_ARRAY
                       ),
        'review' => array(
                        'filter' => FILTER_SANITIZE_SPECIAL_CHARS,
                        'flags'  => FILTER_REQUIRE_ARRAY
                       )
    ));
    $errors = array();
    if ($review['parent'] < 1)
    {
      $errors[] = '  ';
    }
    if ($review['speed'] > 5 || $review['speed'] < 1)
    {
      $errors[] = '   ';
    }
    if ($review['responsibility'] > 5 || $review['responsibility'] < 1)
    {
      $errors[] = '   ';
    }
    if ($review['quality'] > 5 || $review['quality'] < 1)
    {
      $errors[] = '   ';
    }
    if ($review['summary'] > 5 || $review['summary'] < 1)
    {
      $errors[] = '   ';
    }
    if (count($review['language']) < 1 || count($review['review']) < 1)
    {
      $errors[] = '  ';
    }
    foreach ($review['language'] as $lang)
    {
      if ($lang > 2 || $lang < 0)
      {
        $errors[] = '  ';
      }
    }
    foreach ($review['review'] as $reviews)
    {
      if (strlen(trim($reviews)) < 1)
      {
        $errors[] = '  ';
      }
    }
    if (count($errors) === 0)
    {
      $DB->Query("INSERT INTO reviews ( `parent_id`,          `user_id`, `rating_up`, `rating_down`, `rating_speed`,           `rating_responsibility`,         `rating_quality`,         `rating_summary`) 
                         VALUES       ('".$review['parent']."','$user_id', 0,          0,             '".$review['speed']."', '".$review['responsibility']."', '".$review['quality']."', '".$review['summary']."');")->Out();
      $review_id = (int) $DB->Query("SELECT LAST_INSERT_ID() as last")->out()[0]['last'];
      if ($review_id !== 0)
      {
        foreach ($review['language'] as $id => $lang)
        {
          if (isset($review['review'][$id]))
          {
            $DB->Query("INSERT INTO reviews_text (`review_id`, `text`, `language_id`)
                        VALUES ('".$review_id."', '".$review['review'][$id]."', '".$lang."')");
          }
          else
          {
            $errors[] = ' .';
          }
        }
      }
    }
    if (count($errors) > 0)
    {
      echo json_encode(array('success' => false, 'errors' => $errors));
    }
    else
    {
      echo json_encode(array('success' => true));
    }
  }
  if ($type === 'post_comment')
  {
    $comment = filter_input_array(INPUT_POST, array(
      'parent' => FILTER_VALIDATE_INT,
      'comment' => FILTER_SANITIZE_SPECIAL_CHARS
    ));
    $errors = array();
    if ($comment['parent'] < 1)
    {
      $errors[] = '  ';
    }
    if (strlen(trim($comment['comment'])) < 1)
    {
      $errors[] = '  ';
    }
    if (count($errors) === 0)
    {
      $DB->Query("INSERT INTO `comments`(`user_id`, `review_id`, `text`) VALUES ('$user_id', '".$comment['parent']."', '".$comment['comment']."')");
    }
    
    if (count($errors) > 0)
    {
      echo json_encode(array('success' => false, 'errors' => $errors));
    }
    else
    {
      echo json_encode(array('success' => true));
    }
  }
  
  die();
}

